func TestWKBReaderInvalidWkbShouldBeReadable(t *testing.T) {
	wkbReader := jts.Io_NewWKBReaderWithFactory(ioWKBReaderTestGeomFactory)
	// The last sub-geometry uses 2029 unlike others.
	geometry, err := wkbReader.ReadBytes(jts.Io_WKBReader_HexToBytes(""))
	if err != nil {
		t.Fatalf("reading WKB: %v", err)
	}

	junit.AssertTrue(t, java.InstanceOf[*jts.Geom_GeometryCollection](geometry))
	junit.AssertEquals(t, 4326, geometry.GetSRID())

	geometryCollection := java.Cast[*jts.Geom_GeometryCollection](geometry)
	for i := 0; i < geometryCollection.GetNumGeometries()-1; i++ {
		junit.AssertEquals(t, 4326, geometryCollection.GetGeometryN(i).GetSRID())
	}
	lastSubGeometry := geometryCollection.GetGeometryN(geometryCollection.GetNumGeometries() - 1)
	junit.AssertTrue(t, java.InstanceOf[*jts.Geom_Polygon](lastSubGeometry))
	junit.AssertEquals(t, 2029, lastSubGeometry.GetSRID())
}

func TestWKBReaderHugeNumberOfPoints(t *testing.T) {
	// 0: 00 - XDR (Big endian)
	// 1: 00000003 - POLYGON ( 3 )
	// 5: 00000001 - Num Rings = 1
	// 9: 40590000 - Num Points = 1079574528
	checkWKBReaderParseException(t, "00000000030000000140590000000000004069000000000000")
}

func TestWKBReaderNumCoordsNegative(t *testing.T) {
	// 0: 01 - NDR (Little endian)
	// 1: 02000000 - LINESTRING ( 2 )
	// 5: 0000FFFF - Num Points = -65536
	checkWKBReaderParseException(t, "01020000000000FFFF")
}

func TestWKBReaderNumElementsNegative(t *testing.T) {
	// 0: 00 - XDR (Big endian)
	// 1: 00000004 - MULTIPOINT ( 4 )
	// 5: FFFFFFFF - Num Elements = -1
	checkWKBReaderParseException(t, "0000000004FFFFFFFF000000000140590000000000004059000000000000000000000140690000000000004059000000000000")
}

func TestWKBReaderNumRingsNegative(t *testing.T) {
	// 0: 00 - XDR (Big endian)
	// 1: 00000003 - POLYGON ( 3 )
	// 5: FFFFFFFF - Num Rings = -1
	checkWKBReaderParseException(t, "0000000003FFFFFFFF0000000440590000000000004069000000000000405900000000000040590000000000004069000000000000405900000000000040590000000000004069000000000000")
}

func checkWKBReaderParseException(t *testing.T, wkbHex string) {
	t.Helper()
	wkbReader := jts.Io_NewWKBReaderWithFactory(ioWKBReaderTestGeomFactory)
	wkb := jts.Io_WKBReader_HexToBytes(wkbHex)
	_, err := wkbReader.ReadBytes(wkb)
	if err == nil {
		junit.Fail(t, "")
	}
}

func checkWKBReaderGeometry(t *testing.T, wkbHex, expectedWKT string) {
	t.Helper()
	wkbReader := jts.Io_NewWKBReaderWithFactory(ioWKBReaderTestGeomFactory)
	wkb := jts.Io_WKBReader_HexToBytes(wkbHex)
	g2, err := wkbReader.ReadBytes(wkb)
	if err != nil {
		t.Fatalf("reading WKB: %v", err)
	}

	useRdr := ioWKBReaderTestRdr
	if strings.Contains(expectedWKT, "ZM") {
		useRdr = ioWKBReaderTestRdrM
	} else if strings.Contains(expectedWKT, "M(") || strings.Contains(expectedWKT, "M (") {
		useRdr = ioWKBReaderTestRdrM
	}

	expected, err := useRdr.Read(expectedWKT)
	if err != nil {
		t.Fatalf("parsing expected WKT: %v", err)
	}

	isEqual := expected.CompareToWithComparator(g2, ioWKBReaderTestComp2) == 0
	if !isEqual {
		t.Errorf("geometries not equal\nexpected: %s\ngot: %s", expectedWKT, ioWKBReaderTestGeomToWKT(g2))
	}
}

func checkWKBReaderSRID(t *testing.T, wkbHex string, expectedSrid int) {
	t.Helper()
	wkbReader := jts.Io_NewWKBReaderWithFactory(ioWKBReaderTestGeomFactory)
	geometry, err := wkbReader.ReadBytes(jts.Io_WKBReader_HexToBytes(wkbHex))
	if err != nil {
		t.Fatalf("reading WKB: %v", err)
	}

	junit.AssertTrue(t, java.InstanceOf[*jts.Geom_GeometryCollection](geometry))
	junit.AssertEquals(t, expectedSrid, geometry.GetSRID())

	geometryCollection := java.Cast[*jts.Geom_GeometryCollection](geometry)
	for i := 0; i < geometryCollection.GetNumGeometries(); i++ {
		junit.AssertEquals(t, expectedSrid, geometryCollection.GetGeometryN(i).GetSRID())
	}
}

func ioWKBReaderTestGeomToWKT(g *jts.Geom_Geometry) string {
	writer := jts.Io_NewWKTWriter()
	return writer.Write(g)
}
